expressions.minrangex:
{
    if(strmatch(chs("../noiserange"),"positive"))
    {
        return 0;
    }
    if(strmatch(chs("../noiserange"),"negative") || strmatch(chs("../noiserange"),"zcentered"))
    {
        float amp = ch("../amplitudev") * -1;
        if(ch("../amplitudetype")) amp *= ch("../amplitudescalevx");
        return amp;        
    }
    if(strmatch(chs("../noiserange"),"custom") || strmatch(chs("../noiserange"),"minplusrange"))
    {
        float min = ch("../rangeminv");
        if(ch("../rangemintype")) min *= ch("../rangeminscalevx");
        return min;
    }
    if(strmatch(chs("../noiserange"),"midplusminusrange"))
    {
        float mid = ch("../midvaluev");
        if(ch("../midvaluetype")) mid *= ch("../midvaluescalevx");

        float rl = ch("../rangevaluev"); 
        if(ch("../rangevaluetype")) rl *= ch("../rangevaluescalevx");

        return mid - rl;
    }

}

