parameters.code:
#include <aa_utils.h>

#define INBUCKETS 50

int binarySearch(float val; vector2 arr[]; int buckets)
{
    int l = 0;
    int u = buckets;
    while(u - l > 1)
    {
        int m = (u + l) / 2;
        if(val < arr[m][1])
        {
            u = m;
        }
        else
        {
            l = m;
        }
    }

    return l;
}

float remapCDF(float value; int buckets; vector2 D_CDF[]; vector2 N_CDF[])
{
    int index = clamp(floor(value * buckets), 0, buckets - 1);
    float dist = fit(value,
        N_CDF[index][0], N_CDF[index + 1][0],
        N_CDF[index][1], N_CDF[index + 1][1]);

    vector2 des[] = D_CDF;
    index = binarySearch(dist, des, buckets);
    return fit(dist,
        des[index][1], des[index + 1][1],
        des[index][0], des[index + 1][0]);
}

// Store original attribute value
vector attrib_original = attrib_value;

// Remap Vector
if (!use_raw)
{
    __unoise[0] = remapCDF(__unoise[0], INBUCKETS, D_CDF, N_CDF);
    __unoise[1] = remapCDF(__unoise[1], INBUCKETS, D_CDF, N_CDF);
    __unoise[2] = remapCDF(__unoise[2], INBUCKETS, D_CDF, N_CDF);  
}

if (do_remap)
{
    __unoise[0] = chramp("remapramp",__unoise[0]);
    __unoise[1] = chramp("remapramp",__unoise[1]);
    __unoise[2] = chramp("remapramp",__unoise[2]);
}

// Post-Process
if (dobias) __unoise = set(vop_bias(__unoise.x, bias),vop_bias(__unoise.y, bias),vop_bias(__unoise.z, bias));
if (dogain) __unoise = set(vop_gain(__unoise.x, gain),vop_gain(__unoise.y, gain),vop_gain(__unoise.z, gain));
if (dogamma) __unoise = pow(__unoise, gamma);
if (docontrast) {
    __unoise =  (__unoise * (1.0f + contrast)); 
    __unoise -= (contrast / 2.0f);   
}

// Noise Operation
__unoise = fit01(__unoise,min,max);

if (do_blend)
{
    float blend = blendweight;
    if (blendmode == 1) blend = @blendattrib;
    __unoise = lerp(0,__unoise,blend);
}

if (!use_attriblist)
{    
    // Attribute Operation
    if (bound_attrib)
    {    
        if (operation == 0)  attrib_value = __unoise; // Set
        else if (operation == 1) attrib_value  += __unoise; // Add
        else if (operation == 2) attrib_value  -= __unoise; // Subtract
        else if (operation == 3) attrib_value  *= __unoise; // Multiply
        else if (operation == 4) attrib_value  = min(attrib_value, __unoise); // Min
        else if (operation == 5) attrib_value  = max(attrib_value, __unoise); // Max
    }
    else attrib_value = __unoise;

    // Post-Process
    if (do_clampmin)
    {
        attrib_value[0] = attrib_value[0] < clamp_min ? clamp_min : attrib_value[0];
        attrib_value[1] = attrib_value[1] < clamp_min ? clamp_min : attrib_value[1];
        attrib_value[2] = attrib_value[2] < clamp_min ? clamp_min : attrib_value[2];
    }
    if (do_clampmax)
    {
        attrib_value[0] = attrib_value[0] > clamp_max ? clamp_max : attrib_value[0];  
        attrib_value[1] = attrib_value[1] > clamp_max ? clamp_max : attrib_value[1]; 
        attrib_value[2] = attrib_value[2] > clamp_max ? clamp_max : attrib_value[2]; 
    }

    if (do_normalize) attrib_value = normalize(attrib_value);

    __unoise = applyScope(attrib_original,
                      attrib_value,
                      componentscope);
}

