expressions.outputtype:
{
    if(ch("../attribtype")==0) return 0;
    else
    {
        if(strmatch("*/*",strreplace(chs("../attribs")," ","/"))==1) return 0;
        else return 1;
    }   
}

expressions.minrange:
{
    if(strmatch(chs("../noiserange"),"positive"))
    {
        return 0;
    }
    if(strmatch(chs("../noiserange"),"negative") || strmatch(chs("../noiserange"),"zcentered"))
    {
        return ch("../amplitude") * -1;
    }
    if(strmatch(chs("../noiserange"),"custom") || strmatch(chs("../noiserange"),"minplusrange"))
    {
        return ch("../rangemin");
    }
    if(strmatch(chs("../noiserange"),"midplusminusrange"))
    {
        return ch("../midvalue") - ch("../rangevalue");
    }

}

expressions.maxrange:
{
    if(strmatch(chs("../noiserange"),"positive") || strmatch(chs("../noiserange"),"zcentered"))
    {
        return ch("../amplitude");
    }
    if(strmatch(chs("../noiserange"),"negative") || strmatch(chs("../noiserange"),"zcentered")) 
    {
        return 0;
    }
    if(strmatch(chs("../noiserange"),"custom"))
    {
        return ch("../rangemax");
    }
    if(strmatch(chs("../noiserange"),"minplusrange"))
    {
        return ch("../rangemin") + ch("../rangevalue");
    }
    if(strmatch(chs("../noiserange"),"midplusminusrange"))
    {
        return ch("../midvalue") + ch("../rangevalue");
    }

}

