#else
vector dvd;
vector npos = pos;
vector freq = set(1.0f/elementsize.x, 1.0f/elementsize.y, 1.0f/elementsize.z);
vector warp = set(0.0f, 0.0f, 0.0f);
#endif

float distortscale = 1;

//distort = noisetype != 1  ? distort * 0.75 : distort;
droop *= dodroop;
droopdir = normalize(droopdir);
stretch = 1.0/stretch;

// Custom octave iteration
// The main idea behind distortion is to calculate the derivatives of the noise, and 
// use that in the next iteration to distort the position.

// Set Offset
pos -= (offset * elementsize);

for (int i=0; i<max_octaves; i++) {

    npos = pos * freq * stretch + warp + (-droopdir * (v * droop));

    if (noisetype == 0) { // ALLIGATOR      
        v = anoise(npos) * 1.85; // Custom scale value to closer match 0-1 rangedvd

        // Manual derivs
        /*
        float mxoff = anoise(npos + set(-stepsize, 0, 0));
        float myoff = anoise(npos + set(0, -stepsize, 0));
        float mzoff = anoise(npos + set(0, 0, -stepsize));

        float pxoff = anoise(npos + set(stepsize, 0, 0));
        float pyoff = anoise(npos + set(0, stepsize, 0));
        float pzoff = anoise(npos + set(0, 0, stepsize));

        dvdx = (pxoff - mxoff) / stepsize * 2;
        dvdy = (pyoff - myoff) / stepsize * 2;
        dvdz = (pzoff - mzoff) / stepsize * 2; 
        */
    }    
    else if (noisetype == 1) { // PERLIN
        distortscale = 1.0f;
        #if 0
        noised(npos, v, dvd.x, dvd.y, dvd.z, dvd.w);  
        #else
        noised(npos, v, dvd.x, dvd.y, dvd.z);
        #endif        
        if (dofold)
        {           
            v = efit(v, 0.3f, 0.75f, -1.0f, 1.0f);
            dvd *= v;
            v = abs(v);
        }        
        else v = efit(v, 0.3f, 0.7f, 0.0f, 1.0f);
    }
    else if (noisetype == 2) { // SIMPLEX
        distortscale = 0.65f;
        #if 0
        xnoised(npos, v, dvd.x, dvd.y, dvd.z, dvd.w);
        #else
        xnoised(npos, v, dvd.x, dvd.y, dvd.z);
        #endif
        if (dofold)
        {           
            v = efit(v, 0.23f, 0.8f, -1.0f, 1.0f);
            dvd *= v;
            v = abs(v);
        }        
        else v = efit(v, 0.25f, 0.75f, 0.0f, 1.0f);
    }
    else if (noisetype == 3) { // FAST SIMPLEX
        distortscale = 0.4f;
        v = gxnoised(npos, dvd);
        if (dofold)
        {          
            v = efit(v, 0.09f, 1.0f, -1.0f, 1.0f);
            dvd *= v;
            v = abs(v);       
        }       
        else v = efit(v, 0.1f, 0.87f, 0.0f, 1.0f);
    }    

    noise += v * g; // accumulate noise

    max_amp += g;
    //max_amp += 1 / pow(1 / b, i);
    g *= rough; // decrease amplitude
    d *= rough; // distortion scaled by roughness
    droop *= rough;
    freq *= lac; 

    warp += dvd * distort * distortscale * sqrt(d) * 2; 
    stretch = lerp(stretch, set(1.0f, 1.0f, 1.0f), i / float(max_octaves));
    offset += 1;
}

// Normalise noise
noise /= max_amp;

// Post-Process
if (invert) noise = 1 - noise;
if (dobias) noise = vop_bias(noise, bias);
if (dogain) noise = vop_gain(noise, gain);
if (dogamma) noise = pow(noise, gamma);
if (docontrast) {
    noise =  (noise * (1.0f + contrast)); 
    noise -= (contrast / 2.0f);   
}

noise = fit(noise, 1-coverage, 1, 0, 1);
noise *= amp;

