    wnoise = fit(wnoise, werosion, 1.0f, 0.0f, 1.0f);
    wnoise = pow(wnoise, 2.2f) * 5.0f;
    wnoise = fit(cnoise, 1.0f-wnoise, 1.2f, 0.0f, 1.0f);
    noise = lerp(cnoise, wnoise, wblend);
    noise = clamp(noise, 0, 1000);
}
else noise = cnoise;

