parameters.snippet:
// Get pattern
string pattern = chs("pattern");

// 1. Collect All Exisiting Attributes using the given pattern.

// First get a list of existing attributes in the given class.
string class = chs("attribute_class");
string existing_attribs[];
if (class == "detail") existing_attribs = detailintrinsic(1, "globalattributes");
else if (class == "prim") existing_attribs = detailintrinsic(1, "primitiveattributes");    
else if (class == "point") existing_attribs = detailintrinsic(1, "pointattributes");    
else existing_attribs = detailintrinsic(1, "vertexattributes");

string skip_attribs[];
int output_dimension = chi("dimension");

// Compare each existing attribute in the given class to the full given pattern.
foreach (string existing_attrib; existing_attribs)
{
    // If the existing attribute name matches the pattern and it is a Float or Vector attribute with size of 1 or 3.
    int size = attribsize(1, class, existing_attrib);
    if (match(pattern, existing_attrib) &&
        attribtype(1, class, existing_attrib) == 1 &&
        (size == 1 || size == 3))
    {        
        int newpt = addpoint(0,set(0,0,0));
        setpointattrib(0,'name',newpt,existing_attrib);
        setpointattrib(0,'size',newpt,size);
        setpointattrib(0,'exists',newpt,1);

        if (size != output_dimension)
        {
            append(skip_attribs,existing_attrib);    
            setpointattrib(0,'mismatch',newpt,1);
        }
    }
}

s@mismatch_attribs = join(skip_attribs,', ');
i@has_mismatch_attribs = len(skip_attribs) > 0 ? 1 : 0;
// 2. Collect All None Exisiting Attributes that needs to be created using the given pattern.
// Split pattern into individual tokens based on whitespace (spaces,tab, and return)  
string tokens[] = split(pattern);

foreach (string token; tokens)
{
    // If they are valid attribute names
    if (re_match("[_a-zA-Z]+[_a-zA-Z0-9]*", token))
    {
        // Only add them if they do not exist.
        if (!hasattrib(1, class, token))
        {
            int newpt = addpoint(0,set(0,0,0));
            setpointattrib(0,'name',newpt,token);
            setpointattrib(0,'size',newpt,output_dimension);
            setpointattrib(0,'exists',newpt,0);

            if (token == "__noise") i@keep__noise = 1;
        }
    }   
}

