#include <aa_utils.h>

// Store original attribute value
vector attrib_original = v@attrib;

// Attribute Operation
vector noise = attrib(0,chs("class"),"__noise",@elemnum);

if (chi("displace"))
{
    if(chs("displaceattrib") == "N") noise = v@N * noise[0];
    else noise = v@displaceattrib * noise[0];
}

if (point(1,"exists",0))
{
    int operation = chi("operation");
    if (operation == 0)  v@attrib = noise; // Set
    else if (operation == 1) v@attrib  += noise; // Add
    else if (operation == 2) v@attrib  -= noise; // Subtract
    else if (operation == 3) v@attrib  *= noise; // Multiply
    else if (operation == 4) v@attrib  = min(v@attrib, noise); // Min
    else if (operation == 5) v@attrib  = max(v@attrib, noise); // Max
}
else v@attrib = noise;

if (chi("doclampmin"))
{
    v@attrib[0] = v@attrib[0] < chf("clampminvalue") ? chf("clampminvalue") : v@attrib[0];
    v@attrib[1] = v@attrib[1] < chf("clampminvalue") ? chf("clampminvalue") : v@attrib[1];
    v@attrib[2] = v@attrib[2] < chf("clampminvalue") ? chf("clampminvalue") : v@attrib[2];
}
if (chi("doclampmax"))
{
    v@attrib[0] = v@attrib[0] > chf("clampmaxvalue") ? chf("clampmaxvalue") : v@attrib[0];  
    v@attrib[1] = v@attrib[1] > chf("clampmaxvalue") ? chf("clampmaxvalue") : v@attrib[1]; 
    v@attrib[2] = v@attrib[2] > chf("clampmaxvalue") ? chf("clampmaxvalue") : v@attrib[2]; 
}
if (chi("normalize")) v@attrib = normalize(v@attrib);

v@attrib = applyScope(attrib_original,
                  v@attrib,
                  chs("componentscope"));

